import { useState } from "react";

const classifyEmotion = () => {
  const [tip, setTip] = useState("");

  if (happiness >= 8 && energy >= 8 && motivation >= 8) {
    tip = "Excellent! Focus on challenging tasks, creative work, or starting new projects.";
  } else if (happiness <= 4 && anger >= 6 && anxiety >= 6 && motivation <= 4) {
    tip = "Potentially a difficult day. Practice self-care, relaxation techniques, and break down tasks into small steps.";
  } else if (energy <= 4 && motivation <= 4 && happiness >= 5) {
    tip = "Good for light tasks, planning, reading, or taking breaks.";
  } else if (energy >= 8 && happiness <= 4 && frustration >= 6) {
    tip = "Try physical activity to burn off energy and frustration, or listen to music.";
  } else {
    tip = "Take a moment to reflect on your emotions and choose a task that aligns with your current state.";
  }

  setTip(newTip); // For now, we'll just alert the tip. You can replace this with a proper display later.
};
export default classifyEmotion;
