import React, { useState } from "react";
import "./App.css";

const App = () => {
  // State for emotion scores
  const [happiness, setHappiness] = useState(5);
  const [energy, setEnergy] = useState(5);
  const [anger, setAnger] = useState(5);
  const [anxiety, setAnxiety] = useState(5);
  const [motivation, setMotivation] = useState(5);
  const [tip, setTip] = useState("");

  // Function to handle slider changes
  const handleSliderChange = (setter) => (e) => {
    setter(parseInt(e.target.value, 10));
  };

  // Function to generate productivity tip
  const generateTip = () => {
    if (happiness >= 7 && energy >= 7 && motivation >= 7) {
      setTip("You're on fire! Tackle your most challenging tasks today.");
    } else if (happiness <= 3 && anger >= 6 && anxiety >= 6 && motivation <= 3) {
      setTip("Take a break and focus on self-care. Try deep breathing or meditation.");
    } else if (energy <= 4 && motivation <= 4) {
      setTip("Focus on light tasks or planning. Avoid demanding work.");
    } else if (anger >= 7) {
      setTip("Do a physical activity or listen to music to release stress.");
    } else {
      setTip("Prioritize your tasks and focus on one thing at a time.");
    }
  };

  return (
    <div className="App">
      <h1>Emotion-Based Productivity Tips</h1>
      <div className="slider-container">
        <div className="slider">
          <label>Happiness: {happiness}</label>
          <input type="range" min="1" max="10" value={happiness} onChange={handleSliderChange(setHappiness)} />
        </div>
        <div className="slider">
          <label>Energy: {energy}</label>
          <input type="range" min="1" max="10" value={energy} onChange={handleSliderChange(setEnergy)} />
        </div>
        <div className="slider">
          <label>Anger: {anger}</label>
          <input type="range" min="1" max="10" value={anger} onChange={handleSliderChange(setAnger)} />
        </div>
        <div className="slider">
          <label>Anxiety: {anxiety}</label>
          <input type="range" min="1" max="10" value={anxiety} onChange={handleSliderChange(setAnxiety)} />
        </div>
        <div className="slider">
          <label>Motivation: {motivation}</label>
          <input type="range" min="1" max="10" value={motivation} onChange={handleSliderChange(setMotivation)} />
        </div>
      </div>
      <button className="generate-button" onClick={generateTip}>
        Get Productivity Tip
      </button>
      {tip && (
        <div className="tip-container">
          <div className="tip">{tip}</div>
        </div>
      )}
    </div>
  );
};

export default App;
