import React, { useState } from "react";

const App = () => {
  const [happiness, setHappiness] = useState(5);
  const [energy, setEnergy] = useState(5);
  const [motivation, setMotivation] = useState(5);
  const [anger, setAnger] = useState(5);
  const [anxiety, setAnxiety] = useState(5);
  const [frustration, setFrustration] = useState(5);
  const [tip, setTip] = useState("");

  const handleSliderChange = (setter) => (e) => {
    setter(parseInt(e.target.value, 10));
  };

  const classifyEmotion = () => {
    let newTip = "";

    if (happiness >= 8 && energy >= 8 && motivation >= 8) {
      newTip = "Excellent! Focus on challenging tasks, creative work, or starting new projects.";
    } else if (happiness <= 4 && anger >= 6 && anxiety >= 6 && motivation <= 4) {
      newTip = "Potentially a difficult day. Practice self-care, relaxation techniques, and break down tasks into small steps.";
    } else if (energy <= 4 && motivation <= 4 && happiness >= 5) {
      newTip = "Good for light tasks, planning, reading, or taking breaks.";
    } else if (energy >= 8 && happiness <= 4 && frustration >= 6) {
      newTip = "Try physical activity to burn off energy and frustration, or listen to music.";
    } else {
      newTip = "Take a moment to reflect on your emotions and choose a task that aligns with your current state.";
    }

    setTip(newTip);
  };

  return (
    <div className="App">
      <h1>Emotion-Based Productivity Tips</h1>
      <div>
        <label>Happiness: {happiness}</label>
        <input type="range" min="1" max="10" value={happiness} onChange={handleSliderChange(setHappiness)} />
      </div>
      <div>
        <label>Energy: {energy}</label>
        <input type="range" min="1" max="10" value={energy} onChange={handleSliderChange(setEnergy)} />
      </div>
      <div>
        <label>Motivation: {motivation}</label>
        <input type="range" min="1" max="10" value={motivation} onChange={handleSliderChange(setMotivation)} />
      </div>
      <div>
        <label>Anger: {anger}</label>
        <input type="range" min="1" max="10" value={anger} onChange={handleSliderChange(setAnger)} />
      </div>
      <div>
        <label>Anxiety: {anxiety}</label>
        <input type="range" min="1" max="10" value={anxiety} onChange={handleSliderChange(setAnxiety)} />
      </div>
      <div>
        <label>Frustration: {frustration}</label>
        <input type="range" min="1" max="10" value={frustration} onChange={handleSliderChange(setFrustration)} />
      </div>
      <button onClick={classifyEmotion}>Get Productivity Tip</button>
      {tip && <div className="tip">{tip}</div>}
    </div>
  );
};

export default App;
