export const analyzeEmotionWithGemini = async (happiness, energy, anger, anxiety, motivation) => {
  const apiKey = "AIzaSyDyuSW159pD4ApqkKNVr_V1lbYwVSXhYT0"; // Replace with your actual API key
  const url = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`;

  const prompt = `The user has the following emotional scores:
    - Happiness: ${happiness}
    - Energy: ${energy}
    - Anger: ${anger}
    - Anxiety: ${anxiety}
    - Motivation: ${motivation}
    Provide a personalized productivity tip and actionable steps.`;

  const response = await fetch(url, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      contents: [
        {
          parts: [
            {
              text: prompt,
            },
          ],
        },
      ],
    }),
  });

  if (!response.ok) {
    throw new Error("Failed to fetch data from Gemini API");
  }

  const data = await response.json();
  return data.candidates[0].content.parts[0].text; // Extract the generated text
};
