import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [happiness, setHappiness] = useState(5);
  const [energy, setEnergy] = useState(5);
  const [anger, setAnger] = useState(5);
  const [anxiety, setAnxiety] = useState(5);
  const [motivation, setMotivation] = useState(5);
  const [tip, setTip] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // const analyzeEmotionWithGemini = async (happiness, energy, anger, anxiety, motivation) => {
  //   const apiKey = "YOUR_GEMINI_API_KEY";
  //   const url = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`;

  //   const prompt = `Provide a productivity tip and 3 actionable steps based on these emotional scores:
  //   - Happiness: ${happiness}
  //   - Energy: ${energy}
  //   - Anger: ${anger}
  //   - Anxiety: ${anxiety}
  //   - Motivation: ${motivation}

  //   Format the response in HTML as follows:
  //   <ul>
  //     <li><strong>Tip:</strong> [Your tip here]</li>
  //     <li><strong>Steps:</strong>
  //       <ol>
  //         <li>[Step 1]</li>
  //         <li>[Step 2]</li>
  //         <li>[Step 3]</li>
  //       </ol>
  //     </li>
  //   </ul>`;

  //   const response = await fetch(url, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({
  //       contents: [
  //         {
  //           parts: [
  //             {
  //               text: prompt,
  //             },
  //           ],
  //         },
  //       ],
  //       generationConfig: {
  //         maxOutputTokens: 150, // Limit response length
  //       },
  //     }),
  //   });

  //   if (!response.ok) {
  //     throw new Error("Failed to fetch data from Gemini API");
  //   }

  //   const data = await response.json();
  //   return data.candidates[0].content.parts[0].text;
  // };

  const generateTip = async () => {
    setIsLoading(true);
    try {
      const response = await analyzeEmotionWithGemini(happiness, energy, anger, anxiety, motivation);
      setTip(response);
    } catch (error) {
      console.error("Error generating tip:", error);
      setTip("Failed to generate a tip. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <div className="App">
        <h1>Emotion-Based Productivity Tips</h1>
        <div className="slider-container">
          <div className="slider">
            <label>Happiness: {happiness}</label>
            <input
              type="range"
              min="1"
              max="10"
              value={happiness}
              onChange={handleSliderChange(setHappiness)}
              style={{
                background: `linear-gradient(to right, ${getSliderColor(1, "happiness")}, ${getSliderColor(10, "happiness")})`,
              }}
            />
          </div>
          <div className="slider">
            <label>Energy: {energy}</label>
            <input
              type="range"
              min="1"
              max="10"
              value={energy}
              onChange={handleSliderChange(setEnergy)}
              style={{
                background: `linear-gradient(to right, ${getSliderColor(1, "energy")}, ${getSliderColor(10, "energy")})`,
              }}
            />
          </div>
          <div className="slider">
            <label>Anger: {anger}</label>
            <input
              type="range"
              min="1"
              max="10"
              value={anger}
              onChange={handleSliderChange(setAnger)}
              style={{
                background: `linear-gradient(to right, ${getSliderColor(1, "anger")}, ${getSliderColor(10, "anger")})`,
              }}
            />
          </div>
          <div className="slider">
            <label>Anxiety: {anxiety}</label>
            <input
              type="range"
              min="1"
              max="10"
              value={anxiety}
              onChange={handleSliderChange(setAnxiety)}
              style={{
                background: `linear-gradient(to right, ${getSliderColor(1, "anxiety")}, ${getSliderColor(10, "anxiety")})`,
              }}
            />
          </div>
          <div className="slider">
            <label>Motivation: {motivation}</label>
            <input
              type="range"
              min="1"
              max="10"
              value={motivation}
              onChange={handleSliderChange(setMotivation)}
              style={{
                background: `linear-gradient(to right, ${getSliderColor(1, "motivation")}, ${getSliderColor(10, "motivation")})`,
              }}
            />
          </div>
        </div>
        <button className="generate-button" onClick={generateTip}>
          Get Productivity Tip
        </button>
        {tip && (
          <div className="tip-container">
            <div className="tip">{tip}</div>
          </div>
        )}
      </div>
      {tip && <TipDisplay tip={tip} />}
    </div>
  );
};

const TipDisplay = ({ tip }) => {
  return <div className="tip-container" dangerouslySetInnerHTML={{ __html: tip }} />;
};

export default App;
